INSERT INTO `productinfo` VALUES (27, 21, 0, 1, '腾岩科技为您量身定做各类网站', '腾岩科技为您量身定做各类网站，会根据您的需求和您公司的特色，为您订制特色网站。\r\n\r\n网站采用的技术是PHP语言+MYSQL数据库，可在WINDOWS，LINUX，UNIX，CENTOS多种服务器上跨平台运行。\r\n', '腾岩科技为您量身定做各类网站，会根据您的需求和您公司的特色，为您订制特色网站。<br />\r\n<br />\r\n网站采用的技术是PHP语言+MYSQL数据库，可在WINDOWS，LINUX，UNIX，CENTOS多种服务器上跨平台运行。<br />\r\n<br />\r\n网站整体结构策划及企业形象设计；<br />\r\n&rarr;基础服务: 个性化主页及频道栏目设计（包含公司简介、产品介绍、用户反馈、联系我们）；<br />\r\n&rarr; 精美页面10页，超出部分按100元/页收费（产品介绍页面的产品条目不得超过50）；<br />\r\n&rarr; 网站功能：新闻管理系统、产品管理系统、后台用户管理、招聘管理系统、权限系统、数据库备份、广告管理、版面管理、支持SEO优化、每个新闻和每个产品页面可自定义SEO搜索标签、前台邮件反馈系统、支持第三方统计代码、网站防拷贝、支持GOOGLE地图。<br />\r\n<img width="300" height="208" alt="" src="/upload/other/3370756356.jpg" /><br />\r\n<br />\r\n<br />\r\n详细功能可参考我们的DEMO演示站点:<br />\r\n<a class="link_navi" href="http://demo-1.vicp.net:8080/">http://demo-1.vicp.net:8080/</a><br />\r\n<a class="link_navi" href="http://demo-2.vicp.net:8080/">http://demo-2.vicp.net:8080/</a>', '', 1, 1, '2012-02-28 14:14:26', '2012-05-15 17:53:51', '0000-00-00 00:00:00', 0, 27);
